javascript: (function () { const accountName = prompt('Enter account name'); if (!accountName) return; const url = `https://jnjprod.service-now.com/cmdb_ci_aws_account_list.do?sysparm_nostack=true&sysparm_first_row=1&sysparm_query=GOTO123TEXTQUERY321=itx-${accountName}`; fetch(url) .then(response => { if (!response.ok) { throw new Error('Network response was not ok.'); } return response.text(); }) .then(html => { const tempElement = document.createElement('div'); tempElement.innerHTML = html; const accountTableRows = tempElement.querySelectorAll('#cmdb_ci_aws_account_table > tbody > tr > td'); if (accountTableRows.length < 11) { throw new Error('Account information is incomplete.'); } const accountId = accountTableRows[6].innerText; const accountNumber = accountTableRows[5].innerText; const environment = accountTableRows[10].innerText; const details = `Account ID: ${accountId}

Account Number: ${accountNumber}

Environment: ${environment}`; if (accountId) { navigator.clipboard.writeText(accountNumber); console.log(details); alert(details); } else { throw new Error('Account ID or Environment is missing.'); } }) .catch(error => { console.error('Error fetching data:', error); alert('An error occurred: ' + error.message); });})();
